mapper.update:
update sys_property_fund_flow
flow_no = #{flowNo}, community_id = #{communityId}, flow_type = #{flowType}, amount = #{amount}, title = #{title}, description = #{description}, handler = #{handler}, approval_status = #{approvalStatus}, approver_id = #{approverId}, approval_time = #{approvalTime}, approval_note = #{approvalNote}, flow_date = #{flowDate}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where flow_id = #{flowId}

mapper.select:
SELECT
    (
        SELECT IFNULL(SUM(amount),0)
        FROM sys_property_fund_flow
        WHERE flow_type = '3'

            AND community_id = #{communityId}

    ) -
    (
        SELECT IFNULL(SUM(amount),0)
        FROM sys_property_fund_flow
        WHERE flow_type = '4'

            AND community_id = #{communityId}

    ) AS maintenanceFund,
    (
        SELECT IFNULL(SUM(amount),0)
        FROM sys_property_fund_flow
        WHERE flow_type = '1'

            AND community_id = #{communityId}

    ) -
    (
        SELECT IFNULL(SUM(amount),0)
        FROM sys_property_fund_flow
        WHERE flow_type = '2'

            AND community_id = #{communityId}

    ) AS operatingIncome,
    (
        SELECT IFNULL(SUM(amount),0)
        FROM sys_property_fund_flow
        WHERE flow_type = '3'
          AND DATE_FORMAT(flow_date, '%Y-%m') = DATE_FORMAT(CURDATE(), '%Y-%m')

            AND community_id = #{communityId}

    ) AS maintenanceIncrease,
    (
        SELECT IFNULL(SUM(amount),0)
        FROM sys_property_fund_flow
        WHERE flow_type = '1'
          AND DATE_FORMAT(flow_date, '%Y-%m') = DATE_FORMAT(CURDATE(), '%Y-%m')

            AND community_id = #{communityId}

    ) AS operatingIncrease

SELECT ( SELECT IFNULL(SUM(amount),0) FROM sys_property_fund_flow WHERE flow_type IN ('1', '3') AND DATE_FORMAT(flow_date, '%Y-%m') = DATE_FORMAT(CURDATE(), '%Y-%m')
AND community_id = #{communityId}
) AS income, ( SELECT IFNULL(SUM(amount),0) FROM sys_property_fund_flow WHERE flow_type IN ('2', '4') AND DATE_FORMAT(flow_date, '%Y-%m') = DATE_FORMAT(CURDATE(), '%Y-%m')
AND community_id = #{communityId}
) AS expense, ( SELECT IFNULL(SUM(amount),0) FROM sys_property_fund_flow WHERE flow_type IN ('1', '3') AND DATE_FORMAT(flow_date, '%Y-%m') = DATE_FORMAT(CURDATE(), '%Y-%m')
AND community_id = #{communityId}
) - ( SELECT IFNULL(SUM(amount),0) FROM sys_property_fund_flow WHERE flow_type IN ('2', '4') AND DATE_FORMAT(flow_date, '%Y-%m') = DATE_FORMAT(CURDATE(), '%Y-%m')
AND community_id = #{communityId}
) AS balance, ( SELECT COUNT(*) FROM sys_property_fund_flow WHERE DATE_FORMAT(flow_date, '%Y-%m') = DATE_FORMAT(CURDATE(), '%Y-%m')
AND community_id = #{communityId}
) AS transactionCount
