mapper.select:
SELECT m.meeting_id, m.meeting_title, m.sub_title, m.community_id, c.community_name, m.meeting_type, m.meeting_tag, m.selection_total, m.selection_count, m.meeting_content, m.meeting_time, m.meeting_location, m.meeting_status, m.vote_start_time, m.vote_end_time, m.cover_image, m.create_by, m.create_time, m.update_by, m.update_time, m.show_participant_count FROM sys_property_meeting m LEFT JOIN sys_estate_community c ON m.community_id = c.community_id
and m.meeting_title like concat('%', #{meetingTitle}, '%') and m.community_id = #{communityId} and m.meeting_type = #{meetingType} and m.meeting_tag = #{meetingTag} and m.meeting_content = #{meetingContent} and m.meeting_time = #{meetingTime} and m.meeting_location like concat('%', #{meetingLocation}, '%') and m.meeting_status = #{meetingStatus} and m.vote_start_time = #{voteStartTime} and m.vote_end_time = #{voteEndTime}
order by m.meeting_id desc

mapper.update:
update sys_property_meeting
meeting_title = #{meetingTitle}, sub_title = #{subTitle}, community_id = #{communityId}, meeting_type = #{meetingType}, meeting_tag = #{meetingTag}, selection_total = #{selectionTotal}, selection_count = #{selectionCount}, meeting_content = #{meetingContent}, meeting_time = #{meetingTime}, meeting_location = #{meetingLocation}, meeting_status = #{meetingStatus}, vote_start_time = #{voteStartTime}, vote_end_time = #{voteEndTime}, total_voters = #{totalVoters}, cover_image = #{coverImage}, congduo = #{congduo}, selected_properties = #{selectedProperties}, pass_standard = #{passStandard}, normal_participation_rate = #{normalParticipationRate}, normal_approval_rate = #{normalApprovalRate}, high_participation_rate = #{highParticipationRate}, high_approval_rate = #{highApprovalRate}, show_participant_count = #{showParticipantCount}, actual_voters = #{actualVoters}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where meeting_id = #{meetingId}
